ter, \r\nheader, hgroup, menu, \r\nnav, section { \r\n	display: 				block; \r\n}\r\n\r\n\r\n/********************************************************************\r\n	<A> LINKS\r\n********************************************************************/\r\n\r\na  { \r\n	color: 					#0090d3; \r\n	text-decoration: 		none; \r\n}\r\n\r\na:hover 	{ \r\n	text-decoration: 		underline; \r\n}\r\n\r\na:active { }\r\na:visited { }\r\n\r\n/* These get a fat dotted underline on hover */\r\n.post-bodycopy a,\r\n.widget a,\r\n.singlenav a,\r\n.commentlist a,\r\n.logged-in-as a  { \r\n	border-bottom: 		dotted 2px transparent; /* \"invisible\" placeholder for link hover color */\r\n}\r\n\r\n.post-bodycopy a:hover,\r\n.widget a:hover,\r\n.singlenav a:hover,\r\n.commentlist a:hover,\r\n.logged-in-as a:hover {\r\n	border-bottom-color: #0090d3;\r\n	text-decoration: 		none;\r\n	color: 					#0090d3;	\r\n}\r\n\r\n\r\n/********************************************************************\r\n	Animation\r\n	Add all CSS selectors that should be animated on hover\r\n********************************************************************/\r\n\r\na,\r\n.widget > h3, \r\n.widget > h3 i,\r\n.ed_button,\r\n#submit,\r\n.thumb-shadow,\r\n.firstpart,\r\n#menu1 i,\r\n.commentlist li > div,\r\n.commentlist .avatar {\r\n	transition: 			all 0.5s ease-out;\r\n	-webkit-transition: 	all 0.5s ease-out;\r\n	-moz-transition: 		all 0.5s ease-out;\r\n	-o-transition: 		all 0.5s ease-out;\r\n}	\r\n\r\n\r\n/********************************************************************\r\n	<H1> - <H6> HEADINGS\r\n********************************************************************/\r\n\r\nh1, h2, h3, h4, h5, h6 {\r\n	font-family: 			\'Yanone Kaffeesatz\', sans-serif;\r\n	font-weight: 			normal; \r\n	margin: 					20px 0 10px 0;\r\n}\r\n\r\nh1 { font-size: 40px; line-height: 44px; }\r\nh2 { font-size: 36px; line-height: 40px; }\r\nh3 { font-size: 32px; line-height: 35px; }\r\nh4 { font-size: 28px; line-height: 33px; }\r\nh5 { font-size: 24px; line-height: 27px; }\r\nh6 { font-size: 20px; line-height: 22px; }\r\n\r\n\r\n/********************************************************************\r\n	<IMG> IMAGES\r\n********************************************************************/\r\n\r\na img { border: none; }\r\n\r\n.wp-caption {\r\n}\r\n\r\n.wp-caption.alignleft,\r\n.wp-caption.alignnone {\r\n	margin-left: 			-9px;\r\n}\r\n\r\n.wp-caption.alignright {\r\n	margin-right: 			-9px;\r\n}\r\n\r\n.wp-caption img {\r\n	background: 			#fff; \r\n	padding: 				10px; \r\n	border: 					solid 2px #cccccc;\r\n}\r\n\r\n.wp-caption-text 	{ \r\n	color:					#999999;\r\n	font-size: 				14px; \r\n	text-align: 			center; \r\n	padding: 				5px; \r\n	margin-bottom:			0; /* this is a p element, turn off its bottom margin */\r\n}\r\n\r\niframe .player-container {\r\n	border: 					solid 3px #000000;\r\n}\r\n\r\n.alignleft { \r\n	float: 					left; \r\n	margin: 					5px 15px 10px 0; \r\n}\r\n\r\n.alignright { \r\n	float: 					right; \r\n	margin: 					5px 0 10px 15px; \r\n}\r\n\r\n.aligncenter { \r\n	display: 				block; \r\n	margin: 					0 auto; \r\n}\r\n\r\n.gallery-caption {\r\n}\r\n\r\n/* images inside posts on singular pages */\r\nbody.page .hentry img,\r\nbody.single .hentry img {\r\n	padding: 				7px;\r\n	border: 					solid 1px #dddddd;\r\n}\r\n\r\n\r\n/********************************************************************\r\n Resizing content\r\n********************************************************************/\r\n\r\n.widget, .hentry {\r\n	word-wrap: 				break-word;\r\n}\r\n\r\ninput, \r\ntextarea {\r\n	max-width: 				100%;\r\n}\r\n\r\nimg { \r\n	max-width: 				100%; \r\n	height: 					auto; \r\n	display: 				block;\r\n	width: 					auto; /* makes IE8 keep aspect ratio, adjusts the height */\r\n}\r\n\r\nimg.wp-smiley {\r\n	display: 				inline; /* reset display:block for smileys */\r\n}\r\n\r\n.wp-caption {\r\n	max-width: 				100% !important; 	/* WP adds \'width=\"...\"\' inline to images with .wp-caption, and \r\n										